$(document).ready(function() {
    $('a[href*="#"]:not([href="#img01"]):not([href="#img02"]):not([href="#img03"]):not([href="#img04"]):not([href="#img05"]):not([href="#img06"]):not([href="#img07"]):not([href="#img08"]):not([href="#img09"]):not([href="#img10"]):not([href="#img11"]):not([href="#img12"]):not([href="#img13"]):not([href="#img14"]):not([href="#img15"]):not([href="#img16"]):not([href="#lbclose1"]):not([href="#lbclose2"]):not([href="#lbclose3"]):not([href="#lbclose4"])').click(function() {
      if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
        var target = $(this.hash);
        target = target.length ? target : $('[name=' + this.hash.slice(1) +']');
        if (target.length) {
          $('html, body').animate({
            scrollTop: target.offset().top
          }, 1000);
          return false;
        }
      }
    });
  });
