$(document).ready(function(){
  $('.Boskotainer').slick({
  autoplay: true,
  autoplaySpeed: 7000,
  pauseOnHover: false,
  pauseOnFocus: false,
  draggable: false,
  dots: false,
  arrows: true,
  infinite: true,
  speed: 1000,
  fade: true,
  verticalSwiping: false,
  touchThreshold: 6

});
  
  $('.pause').on('click', function() {
    $('.slider')
        .slick('slickPause')
});

$('.play').on('click', function() {
    $('.slider')
        .slick('slickPlay')
});
});
