$(document).ready(function(){
 $(".Boskotainer").slick({
	dots: true,
	fade: true,
	arrows: true,
	infinite: true,
	autoplay: true,
	autoplaySpeed: 7000,
	pauseOnFocus: false,
	pauseOnHover: false,
	speed: 300,
	slidesToShow: 1,
	adaptiveHeight: false
});

$('.pauseplay').on('click', function(){
    var $pauseBtn = $(this);
    if ($pauseBtn.hasClass('paused')){
        $(".Boskotainer").slick('slickPlay');
        $pauseBtn.removeClass('paused');
    } else {
        $(".Boskotainer").slick('slickPause');
        $pauseBtn.addClass('paused');
    }
});
});
