$(document).ready(function(){
 $(".Boskotainer").slick({
					dots: true,
					fade: true,
					pauseOnHover: false,
					arrows: true,
					infinite: true,
					autoplay: true,
					autoplaySpeed: 1000,
					speed: 300,
					slidesToShow: 1,
					adaptiveHeight: false
});

$('.pause').on('click', function() {
    $('.Boskotainer')
        .slick('slickPause')
});

$('.play').on('click', function() {
    $('.Boskotainer')
        .slick('slickPlay')
});
});
