$(document).ready(function(){
  $('.Boskotainer').slick({
  autoplay: true,
  autoplaySpeed: 7000,
  pauseOnHover: false,
  dots: false,
  arrows: true,
  infinite: true,
  speed: 1000,
  fade: true,
  verticalSwiping: false,
  slidesToShow: 1,
  touchThreshold: 6

});
});
  
$(document).ready(function(){  
  $('.pause').on('click', function() {
    $('.Boskotainer')
        .slick('slickPause')
});
});

$(document).ready(function(){  
$('.play').on('click', function() {
    $('.Boskotainer')
        .slick('slickPlay')
});
});
